mapper.select:
SELECT mu.menu_id, mu.parent_id, mu.name, mu.path, mu.remark, mu.priority, is_enable, (CASE WHEN mu.is_enable = 0 then '无效' else '有效' END ) as isEnableName, mu.is_admin, mu.update_time, mu.create_time, GROUP_CONCAT(priv.priv_id) priv_id, GROUP_CONCAT(priv.priv_name) priv_name from mcs_menu mu left join mcs_menu_priv as priv on mu.menu_id=priv.menu_id
mu.parent_id = #{parentid}
group by mu.menu_id ORDER BY mu.priority

mapper.update:
update mcs_menu
name = #{name}, path = #{path}, remark = #{remark}, priority = #{priority}, is_enable = #{isEnable}, update_time = now()
where menu_id = #{menuid}
