mapper.select:
SELECT mu.menu_id, mu.parent_id, mu.name, mu.path, mu.remark, mu.priority, mu.icon, is_enable, mu.is_admin, mu.update_time, mu.create_time, GROUP_CONCAT(priv.priv_id) priv_id from mcs_menu mu left join mcs_menu_priv as priv on mu.menu_id=priv.menu_id
mu.parent_id = #{parentid}
group by mu.menu_id ORDER BY mu.priority

mapper.update:
update mcs_menu
name = #{name}, path = #{path}, remark = #{remark}, is_admin = #{isAdmin}, icon = #{iconCls}, priority = #{priority}, is_enable = #{isEnable}, update_time = now()
where menu_id = #{menuid}
